import React, { useState, useRef, useEffect } from 'react';
import { supabase } from '../../lib/supabase';

interface ArticleFormData {
  title: string;
  content: string;
  category: string;
  summary: string;
  cover_image: string;
}

const ArticleEditor = () => {
  const [formData, setFormData] = useState<ArticleFormData>({
    title: '',
    content: '',
    category: '帝国攻略',
    summary: '',
    cover_image: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState<{type: 'success' | 'error', text: string} | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const categories = [
    '帝国攻略',
    '鼠人战术',
    '混沌入门',
    '通用教程'
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage(null);

    try {
      const { data, error } = await supabase
        .from('articles')
        .insert([{
          ...formData,
          views: 0,
        }]);

      if (error) throw error;

      setMessage({ type: 'success', text: '文章发布成功！' });
      setFormData({
        title: '',
        content: '',
        category: '帝国攻略',
        summary: '',
        cover_image: ''
      });
    } catch (error) {
      setMessage({ type: 'error', text: '发布失败，请重试' });
      console.error('Error publishing article:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleImageUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploading(true);
    setMessage(null);

    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${Math.random().toString(36).substring(2)}-${Date.now()}.${fileExt}`;
      const filePath = `public/${fileName}`; // Ensure the file is uploaded to the 'public' folder

      const { data, error } = await supabase.storage
        .from('article-images') // Ensure this is your correct bucket name
        .upload(filePath, file, {
          cacheControl: '3600',
          upsert: false
        });

      if (error) {
        console.error('Upload error details:', error);
        throw error;
      }

      const imageUrl = `${supabase.storageUrl}/storage/v1/object/public/article-images/${filePath}`;
      setFormData(prev => ({
        ...prev,
        cover_image: imageUrl
      }));

      setMessage({ type: 'success', text: '图片上传成功！' });
    } catch (error: any) {
      console.error('Error uploading image:', error);
      setMessage({ 
        type: 'error', 
        text: `图片上传失败: ${error.message || '请重试'}` 
      });
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="admin-editor">
      <h1 className="editor-title">发布新文章</h1>
      
      {message && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      <form onSubmit={handleSubmit} className="editor-form">
        <div className="form-group">
          <label htmlFor="title">标题</label>
          <input
            type="text"
            id="title"
            value={formData.title}
            onChange={(e) => setFormData(prev => ({...prev, title: e.target.value}))}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">分类</label>
          <select
            id="category"
            value={formData.category}
            onChange={(e) => setFormData(prev => ({...prev, category: e.target.value}))}
          >
            {categories.map(category => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="cover_image">封面图片</label>
          <div className="image-upload">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              ref={fileInputRef}
              className="file-input"
            />
            {isUploading && <span className="upload-status">上传中...</span>}
            
            {formData.cover_image && (
              <div className="image-preview">
                <img src={formData.cover_image} alt="封面预览" />
                <button
                  type="button"
                  onClick={() => {
                    setFormData(prev => ({ ...prev, cover_image: '' }));
                  }}
                  className="remove-image"
                >
                  移除封面
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="summary">摘要</label>
          <textarea
            id="summary"
            value={formData.summary}
            onChange={(e) => setFormData(prev => ({...prev, summary: e.target.value}))}
            rows={3}
          />
        </div>

        <div className="form-group">
          <label htmlFor="content">正文内容</label>
          <textarea
            id="content"
            value={formData.content}
            onChange={(e) => setFormData(prev => ({...prev, content: e.target.value}))}
            rows={10}
            required
          />
        </div>

        <button 
          type="submit" 
          className="submit-button"
          disabled={isSubmitting}
        >
          {isSubmitting ? '发布中...' : '发布文章'}
        </button>
      </form>
    </div>
  );
};

export default ArticleEditor;
